"use strict";(self.webpackChunkupsy_desky_docs=self.webpackChunkupsy_desky_docs||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Introduction","href":"/docs/introduction","docId":"introduction","unlisted":false},{"type":"link","label":"Getting Started","href":"/docs/getting-started","docId":"getting-started","unlisted":false},{"type":"link","label":"BYOE (Bring-Your-Own-Enclosure)","href":"/docs/byoe","docId":"byoe","unlisted":false},{"type":"category","label":"Configuration","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Decoders","href":"/docs/configuration/decoders/","docId":"configuration/decoders/index","unlisted":false},{"type":"link","label":"Presets","href":"/docs/configuration/presets/","docId":"configuration/presets/index","unlisted":false},{"type":"link","label":"Renaming the Device","href":"/docs/configuration/renaming/","docId":"configuration/renaming/index","unlisted":false}],"href":"/docs/configuration/"},{"type":"category","label":"Integrations","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Home Assistant","href":"/docs/integrations/home-assistant","docId":"integrations/home-assistant","unlisted":false},{"type":"link","label":"Hubitat","href":"/docs/integrations/hubitat","docId":"integrations/hubitat","unlisted":false},{"type":"link","label":"Stream Deck","href":"/docs/integrations/stream-deck","docId":"integrations/stream-deck","unlisted":false},{"type":"link","label":"Time-Based","href":"/docs/integrations/time-based","docId":"integrations/time-based","unlisted":false}],"href":"/docs/integrations/"},{"type":"link","label":"Firmware Updates","href":"/docs/firmware-updates","docId":"firmware-updates","unlisted":false},{"type":"category","label":"Troubleshooting & Support","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Connection Issues","href":"/docs/troubleshooting/connection-issues","docId":"troubleshooting/connection-issues","unlisted":false}],"href":"/docs/troubleshooting/"},{"type":"category","label":"Reference","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"HTTP API","href":"/docs/reference/api","docId":"reference/api","unlisted":false},{"type":"category","label":"Desk Compatibility","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Assessing Compatibility","href":"/docs/reference/compatibility/assessing-compatibility","docId":"reference/compatibility/assessing-compatibility","unlisted":false}],"href":"/docs/reference/compatibility/"},{"type":"link","label":"Entities","href":"/docs/reference/entities","docId":"reference/entities","unlisted":false},{"type":"link","label":"GPIO Pinout","href":"/docs/reference/gpio","docId":"reference/gpio","unlisted":false},{"type":"link","label":"Schematics","href":"/docs/reference/schematics/","docId":"reference/schematics/index","unlisted":false}]},{"type":"category","label":"Advanced","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Bluetooth Proxy","href":"/docs/advanced/bluetooth-proxy","docId":"advanced/bluetooth-proxy","unlisted":false},{"type":"link","label":"Firmware Customization","href":"/docs/advanced/customization/","docId":"advanced/customization/index","unlisted":false},{"type":"link","label":"Reverse Engineering Guide","href":"/docs/advanced/reverse-engineering/","docId":"advanced/reverse-engineering/index","unlisted":false}]}]},"docs":{"advanced/bluetooth-proxy":{"id":"advanced/bluetooth-proxy","title":"Bluetooth Proxy","description":"Starting with version 0.4.1, the stock firmware comes with ESPHome\'s Bluetooth Proxy component, which allows you to use your Upsy Desky to proxy Bluetooth connections to/from Home Assistant, extending the Bluetooth range of your Home Assistant instance.","sidebar":"tutorialSidebar"},"advanced/customization/index":{"id":"advanced/customization/index","title":"Firmware Customization","description":"Since the Upsy Desky\'s firmware is based on ESPHome, making customizations to the firmware is as easy as editing some YAML. The best way to modify the firmware config is by installing the ESPHome Dashboard (or using it via cli).","sidebar":"tutorialSidebar"},"advanced/reverse-engineering/index":{"id":"advanced/reverse-engineering/index","title":"Reverse Engineering Guide","description":"If your desk is not officially supported, or if you are having issues with functionality like getting or setting desk height, presets, etc. and have followed the Troubleshooting steps, then it is very possible that your desk uses a protocol that the firmware does not yet understand.","sidebar":"tutorialSidebar"},"byoe":{"id":"byoe","title":"BYOE (Bring-Your-Own-Enclosure)","description":"If you are building your own Upsy Desky or you simply didn\'t opt to include an enclosure in your order, you can print your own so that everything looks nice and clean.","sidebar":"tutorialSidebar"},"configuration/decoders/index":{"id":"configuration/decoders/index","title":"Decoders","description":"Since firmware 1.0.0, it is no longer required to manually configure the decoder. It will automatically determine the correct one to use during startup. See Auto-Detection for more information.","sidebar":"tutorialSidebar"},"configuration/index":{"id":"configuration/index","title":"Configuration","description":"Once you have connected your Upsy Desky to your network, you can configure it by going to its IP address (port 80), or by visiting the device page in Home Assistant.","sidebar":"tutorialSidebar"},"configuration/presets/index":{"id":"configuration/presets/index","title":"Presets","description":"Most standing desk control boxes have the ability to store presets \u2014 usually up to 4. The Upsy Desky exposes the ability to set and recall these presets through virtual buttons.","sidebar":"tutorialSidebar"},"configuration/renaming/index":{"id":"configuration/renaming/index","title":"Renaming the Device","description":"By default, the name of your Upsy Desky in Home Assistant is upsy-desky-XXXXXX, where XXXXXX is derived from the MAC address of the device. This is not very descriptive, so you can change it to something more meaningful.","sidebar":"tutorialSidebar"},"firmware-updates":{"id":"firmware-updates","title":"Firmware Updates","description":"To get the latest features and bug fixes, you will need to update your Upsy Desky\'s firmware. There are a few ways to do this, and we will go over each of them.","sidebar":"tutorialSidebar"},"getting-started":{"id":"getting-started","title":"Getting Started","description":"This page will guide you through setting up your new Upsy Desky with your home automation system and your standing desk. It should take 5-10 minutes, but probably less.","sidebar":"tutorialSidebar"},"integrations/home-assistant":{"id":"integrations/home-assistant","title":"Home Assistant","description":"Since the firmware is based on ESPHome, Home Assistant can be integrated seamlessly with the Upsy Desky. This is the recommended way to integrate the Upsy Desky with other devices in your home, and the best way to create automations due to the wide range of integrations available for Home Assistant.","sidebar":"tutorialSidebar"},"integrations/hubitat":{"id":"integrations/hubitat","title":"Hubitat","description":"There is budding unofficial support for ESPHome-based projects on Hubitat. Community members have made significant progress integrating their Upsy Desky units with Hubitat, and you can follow along on the forum thread.","sidebar":"tutorialSidebar"},"integrations/index":{"id":"integrations/index","title":"Integrations","description":"Native Integrations","sidebar":"tutorialSidebar"},"integrations/stream-deck":{"id":"integrations/stream-deck","title":"Stream Deck","description":"Josh Olsen has created a Stream Deck plugin which lets you adjust your standing desk\'s height via the Upsy Desky.","sidebar":"tutorialSidebar"},"integrations/time-based":{"id":"integrations/time-based","title":"Time-Based","description":"This integration is a Python script created by community member gabe#9075 on Discord. It allows you to set your desired standing and sitting height, and switch between them after a set amount of time during your workday. It also inludes ICS integration so as not to interrupt you during meetings.","sidebar":"tutorialSidebar"},"introduction":{"id":"introduction","title":"Introduction","description":"What is this thing?","sidebar":"tutorialSidebar"},"reference/api":{"id":"reference/api","title":"HTTP API","description":"The stock firmware enables ESPHome\'s Web Server component, which in turn enables an HTTP API (on port 80). Since the stock firmware exposes a sensor for reporting the current desk height, and a number to set the target height, you can use these simple HTTP endpoints to write your integrations.","sidebar":"tutorialSidebar"},"reference/compatibility/assessing-compatibility":{"id":"reference/compatibility/assessing-compatibility","title":"Assessing Compatibility","description":"If your desk\'s control box model number is not in the compatibility list, this doesn\'t necessarily mean it\'s unsupported, just that nobody has tested it yet. You can assess whether your desk may be compatible using the steps below. If all the conditions are satisfied, you may be eligible to receive a unit for testing, free of charge.","sidebar":"tutorialSidebar"},"reference/compatibility/index":{"id":"reference/compatibility/index","title":"Desk Compatibility","description":"Verified Desks","sidebar":"tutorialSidebar"},"reference/entities":{"id":"reference/entities","title":"Entities","description":"The Upsy Desky firmware exposes many entities by default. This page describes the purpose of each.","sidebar":"tutorialSidebar"},"reference/gpio":{"id":"reference/gpio","title":"GPIO Pinout","description":"For convenience, here is what each pin on the RJ45 jacks maps to on the ESP32\'s GPIO:","sidebar":"tutorialSidebar"},"reference/schematics/index":{"id":"reference/schematics/index","title":"Schematics","description":"The schematics for the Upsy Desky are entirely open source and available for reference. You can also find the KiCad source files on the GitHub repository.","sidebar":"tutorialSidebar"},"troubleshooting/connection-issues":{"id":"troubleshooting/connection-issues","title":"Connection Issues","description":"This guide will assist in troubleshooting connection issues with your Upsy Desky, such as it appearing \\"unknown\\" or \\"unavailable\\" in Home Assistant.","sidebar":"tutorialSidebar"},"troubleshooting/index":{"id":"troubleshooting/index","title":"Troubleshooting & Support","description":"Common Issues","sidebar":"tutorialSidebar"}}}')}}]);